import React from 'react'
import styled from 'styled-components'

const StyledCartContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`

const StyledCard = styled.div`
  padding: 20px;
  font-size: 1.4rem;
`

const EM = styled.span`
  font-style: italic;
`

export default function Cart({ order }) {
  console.log("Cart -> order", order)
  return (
    <StyledCartContainer>
      {order.length !== 0 ?
        <>
          <h2>Congratulations! Your order is on the way!</h2>
          {order.map(o => {
            return (
              <StyledCard>
                Hello {o.customer}! Your <EM>{o.size}</EM> Bot with <EM>{o.toppingsChecked.map(topping => `${topping.name}, `)}</EM> functionality is on its way!
                <br />
                <br />
                {o.instructions ? `Special Instruction:${o.instructions} has also been noted` : ''}

              </StyledCard>
            )
          })}
        </>
        : <h2>Your Cart is empty!</h2>}
    </StyledCartContainer>
  )
}
