import React, { useState, useEffect, Component } from 'react'
import axios from 'axios'
import { FormGroup, Input, Button, Alert } from 'reactstrap'
import { BrowserHistory } from 'react-router-dom'
import { Link, NavLink } from "react-router-dom"
import styled from 'styled-components'




const Form = styled.form`
  max-width: 800px;
  margin: 60px auto;
  border: 2px solid black;
  padding: 20px;
`
const Styledh3 = styled.h3`
  text-align: center;
`
const StyledToppings = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  height: 400px;
  align-content: space-between;
  padding: 20px;
`
const StyledLabel = styled.label`
  padding: 10px 0;
`
const StyledOrderFooter = styled.div`
  display: flex;
  justify-content: space-between;
`

export default function Shop({ order, setOrder }) {

  return (
    <Form style={{ padding: '40px' }} >

      <Styledh3>Shop Premade Bots</Styledh3>

      <p><center>Choose from various <b>Bots</b> below.</center></p>

      <FormGroup>
        <legend>Your Name</legend>
        <Input type="text" placeholder="Your Name" value={formState.customer} onChange={e => handleChange(e)} name="customer" data-cy="customer" />
